function closeAllAccordions(){document.querySelectorAll(".accordion-item").forEach(c=>{c.classList.remove("active")})}document.addEventListener("DOMContentLoaded",(function(){const c=document.querySelectorAll(".accordion-item");c.forEach(c=>{c.addEventListener("click",(function(){this.classList.contains("active")||(closeAllAccordions(),this.classList.add("active"))}))}),c[0].classList.add("active")}));